import React, { useState } from 'react';

import AddingForm from './components/AddingForm';
import ShowResults from './components/ShowResults';

function App() {
	const [checked, setChecked] = useState(false);

	const changeCheck = () => {
		setChecked(!checked);
	};

	const URL = 'https://api.collectapi.com/gasPrice/fromCity?city=warsaw';
	fetch(
		`https://api.exchangerate.host/latest?base=${currencyOne.value}&symbols=${currencyTwo.value}`
	)
		.then((res) => res.json())
		.then((data) => {
			const currency1 = currencyOne.value;
			const currency2 = currencyTwo.value;
			const rate = data.rates[currency2];
			rateInfo.textContent = `1 ${currency1} = ${rate.toFixed(4)} ${currency2}`;
			amountTwo.value = (amountOne.value * rate).toFixed(2);
		});

	const fuelCost = 5;
	const costByDay = 100;
	const USER = {
		distanse: 0,
		year: 2,
		date_start: new Date(),
		date_end: new Date(),
		category: 0,
		city: '',
		petrol: 0,
		available: 2,
	};
	const [userData, setUserData] = useState(USER);
	const addUserData = (data) => {
		setUserData(data);
		changeCheck();
	};
	const datesBetween = () => {
		const diffInMs = userData.date_end - userData.date_start;
		const diffInDays = diffInMs / (1000 * 60 * 60 * 24) + 1;
		return diffInDays;
	};
	const numberOfDays = datesBetween();
	return checked === false ? (
		<AddingForm Change={addUserData} />
	) : (
		<ShowResults
			Change={changeCheck}
			fuelCost={fuelCost}
			dayCost={costByDay}
			numberOfDays={numberOfDays}
			userData={userData}
		/>
	);
}

export default App;
