import React, { useState } from 'react';

import AddingForm from './components/AddingForm';
import ShowResults from './components/ShowResults';

function App() {
	const [checked, setChecked] = useState(false);

	const changeCheck = () => {
		setChecked(!checked);
	};
	const fuelCost = 5;

	const userData = {
		distanse: 0,
		year: 0,
		date_start: new Date(),
		date_end: new Date(),
		category: 0,
		city: '',
		petrol: 0,
		available: 0,
	};

	useState(0)
	const results = {
		cost_distance: 0,
		cost_year: 0,
		cost_category: 0,
		cost_count: 0,
		cost_net: 0,
		cost_gross: 0,
	};
	// results.cost_distance = userData.distanse * fuelCost;
	return checked === false ? (
		<div>
			<AddingForm Change={changeCheck} Data={userData} />
		</div>
	) : (
		<div>
			<ShowResults Change={changeCheck} Data={results} />
		</div>
	);
}

export default App;
