import React, { useState } from 'react';

import AddingForm from './components/AddingForm';
import ShowResults from './components/ShowResults';

function App() {
	const [checked, setChecked] = useState(false);

	const changeCheck = () => {
		setChecked(!checked);
	};

	const URL = 'https://api.collectapi.com/gasPrice/fromCity?city=warsaw';

	var data = null;
	var xhr = new XMLHttpRequest();
	// xhr.open('GET', 'https://api.collectapi.com/gasPrice/fromCity?city=warsaw');
	// xhr.setRequestHeader('content-type', 'application/json');
	// xhr.setRequestHeader(
	// 	'authorization',
	// 	'apikey 6r7tjXAU1KmZ8FC0sBBFQF:5XKeovYpwE4V6NfCDF70oe'
	// );
	// xhr.send(data);
	// console.log(xhr);
	// console.log(data);
	const key = 'Af22S0A4MMIrVDhrVE8RcH9_IhIQrT-vOl4FrOemgbI';
	// fetch(
	// 	`https://geocoder.ls.hereapi.com/search/6.2/geocode.json
	// 	?languages=en-US
	// 	&maxresults=4
	// 	&searchtext=Sunnyvale
	// 	&apiKey=Af22S0A4MMIrVDhrVE8RcH9_IhIQrT-vOl4FrOemgbI`
	// )
	// 	.then((res) => res.json())
	// 	.then((data) => {
	// 		console.log(data);
	// 	});
	GET `https://fuel-v2.cc.api.here.com/
		fuel/stations.json
		?prox=52.516667,13.383333,5000
		&jsoncallback=myCallbackFunction
		&apiKey={key}`
	const fuelCost = 5;
	const costByDay = 100;
	const USER = {
		distanse: 0,
		year: 2,
		date_start: new Date(),
		date_end: new Date(),
		category: 0,
		city: '',
		petrol: 0,
		available: 2,
	};
	const [userData, setUserData] = useState(USER);
	const addUserData = (data) => {
		setUserData(data);
		changeCheck();
	};
	const datesBetween = () => {
		const diffInMs = userData.date_end - userData.date_start;
		const diffInDays = diffInMs / (1000 * 60 * 60 * 24) + 1;
		return diffInDays;
	};
	const numberOfDays = datesBetween();
	return checked === false ? (
		<AddingForm Change={addUserData} />
	) : (
		<ShowResults
			Change={changeCheck}
			fuelCost={fuelCost}
			dayCost={costByDay}
			numberOfDays={numberOfDays}
			userData={userData}
		/>
	);
}

export default App;
