import React, { useState } from 'react';

import './ShowResults.css';

const ShowResults = (props) => {
	const petrolCost = (
		(props.userData.distanse / 100) *
		props.userData.petrol *
		props.fuelCost
	).toFixed(2);
	const costAllDays = (props.numberOfDays * props.dayCost).toFixed(2);

	const years = (new Date().getFullYear() - props.userData.year).toFixed(2);
	const costYears = ((petrolCost + costAllDays) * props.userData.year).toFixed(
		2
	);
	const costAvialable = (
		(petrolCost + costAllDays) *
		props.userData.available
	).toFixed(2);
	const costCategory = (
		(petrolCost + costAllDays + costYears + costAvialable) *
		(props.userData.category - 1)
	).toFixed(2);
	const costNet = (
		petrolCost +
		costAllDays +
		costYears +
		costAvialable +
		costCategory
	).toFixed(2);
	const costGross = (costNet * 1.23).toFixed(2);
	return (
		<div>
			<h1>Łączny koszt wynajmu: {costGross}</h1>
			<ul>
				<li>Koszt paliwa: {petrolCost} </li>
				<li>Koszt spowodowany liczbą dni wypożyczenia: {costAllDays}</li>
				<li>
					Koszt spowodowany czasem posiadania prawa jazdy:
					{costYears}
				</li>
				<li>Koszt marką samochodu: {costCategory}</li>
				<li>
					Koszt liczbą modeli samochodu:
					{costAvialable}
				</li>
				<li>Koszt netto: {costNet} </li>
				<li>Koszt brutto: {costGross} </li>
			</ul>
			<button onClick={props.Change}>Wróć</button>
		</div>
	);
};

export default ShowResults;
