import React, { useState } from 'react';

import AddingForm from './components/AddingForm';
import ShowResults from './components/ShowResults';

function App() {
	const [checked, setChecked] = useState(false);

	const changeCheck = () => {
		setChecked(!checked);
	};
	const fuelCost = 5;
	const costByDay = 100;
	const USER = {
		distanse: 0,
		year: 0,
		date_start: new Date(),
		date_end: new Date(),
		category: 0,
		city: '',
		petrol: 0,
		available: 0,
	};
	// const RESULTS = {
	// 	cost_distance: 0,
	// 	cost_year: 0,
	// 	cost_category: 0,
	// 	cost_count: 0,
	// 	cost_net: 0,
	// 	cost_gross: 0,
	// };
	const [userData, setUserData] = useState(USER);
	// const [results, setResults] = useState(RESULTS);
	const addUserData = (data) => {
		setUserData(data);
		// console.log(userData);
		// const costDistance = parseInt(userData.distanse) * fuelCost;
		// const newResult = {
		// 	...results,
		// 	cost_distance: costDistance,
		// };
		// setResults(newResult);
		changeCheck();
	};
	const datesBetween = () => {
		const diffInMs = userData.date_end - userData.date_start;
		const diffInDays = diffInMs / (1000 * 60 * 60 * 24);
		return diffInDays;
	};
	const checkYears = () => {
		const [answer, setAnswer] = useState(0);
		console.log(new Date().getFullYear());
		console.log(userData.year);
		if (new Date().getFullYear() - userData.year < 3) {
			setAnswer(0.2);
		}
		console.log(answer);
		return answer;
	};
	const numberOfDays = datesBetween();
	const numberOfYears = checkYears();
	return checked === false ? (
		<AddingForm Change={addUserData} />
	) : (
		<ShowResults
			Change={changeCheck}
			fuelCost={fuelCost}
			// distance={userData.distanse}
			dayCost={costByDay}
			numberOfDays={numberOfDays}
			numberOfYears={numberOfYears}
			userData={userData}
		/>
	);
}

export default App;
