import React, { useState } from 'react';

import AddingForm from './components/AddingForm';
import ShowResults from './components/ShowResults';

function App() {
	const [checked, setChecked] = useState(false);

	const changeCheck = () => {
		setChecked(!checked);
	};
	const fuelCost = 5;
	const USER = [
		{
			distanse: 0,
			year: 0,
			date_start: new Date(),
			date_end: new Date(),
			category: 0,
			city: '',
			petrol: 0,
			available: 0,
		},
	];
	const RESULTS = [
		{
			cost_distance: 0,
			cost_year: 0,
			cost_category: 0,
			cost_count: 0,
			cost_net: 0,
			cost_gross: 0,
		},
	];

	const addUserData = (data) => {
		setUserData(data);
		const costDistance = 10 * fuelCost;
		console.log(results)
		console.log(...results)
		const newResult = { ...results, cost_distance: costDistance, cost_year: 0 };
		setResults(newResult);
		console.log(results.cost_year);
		changeCheck();
	};

	const [userData, setUserData] = useState(USER);
	const [results, setResults] = useState(RESULTS);

	// results.cost_distance = userData.distanse * fuelCost;
	return checked === false ? (
		<div>
			<AddingForm Change={addUserData} Data={userData} />
		</div>
	) : (
		<div>
			<ShowResults Change={changeCheck} Data={results} />
		</div>
	);
}

export default App;
